mapper.select:
select * from tbl_lostthings


        typeCode=#{type}


order by publishTime desc
limit #{skipCount},#{pageSize}

select * from tbl_lostthings
name like '%' #{keyword} '%'
order by publishTime desc limit #{skipCount},#{pageSize}

select * from tbl_lostthings where publishTime < #{publishTime}

    and name like '%' #{keyword} '%'


    and typeCode=#{type}

order by publishTime desc
limit 1

select * from tbl_lostthings where publishTime > #{publishTime}

    and name like '%' #{keyword} '%'


    and typeCode=#{type}

order by publishTime asc
limit 1

mapper.delete:
delete from tbl_lostthings
where id in

    #{id}
