mapper.select:
select * from tbl_pickthings


        typeCode=#{type}


order by publishTime desc limit #{skipCount},#{pageSize}

select p.id, p.name, p.pickPlace, p.pickDate, p.typeCode, p.publishTime, p.description, p.img, u.loginAct as user, p.state, p.storagePlace from tbl_pickthings p join tbl_user u on p.user=u.id
name like '%' #{keyword} '%'
order by publishTime desc limit #{skipCount},#{pageSize}

select * from tbl_pickthings where publishTime < #{publishTime}

    and typeCode=#{type}

order by publishTime desc
limit 1

select * from tbl_pickthings where publishTime > #{publishTime}

    and typeCode=#{type}

order by publishTime asc
limit 1

select * from tbl_pickthings where publishTime < #{publishTime}
and name like '%' #{keyword} '%'
order by publishTime desc limit 1

select * from tbl_pickthings where publishTime > #{publishTime}
and name like '%' #{keyword} '%'
order by publishTime asc limit 1

mapper.delete:
delete from tbl_pickthings
where id in

    #{id}
